function popUp(winURL){window.open(winURL,"popup","width=320,height=480")}window.onload=function(){if(!document.getElementById)return false;var link=document.getElementsByTagName("a");for(var i=0;i<link.length;i++){if(link[i].getAttribute("class")=="popup"){link[i].onclick=function(){popUp(this.href);return false}}}}